import { useLocation, useNavigate } from 'react-router-dom';
import { Canvas } from '@react-three/fiber';
import { Environment, OrbitControls } from '@react-three/drei';
import BodyModel from './BodyModel';
import { useEffect, useRef, useState } from 'react';

const renderDiagnosis = (diagnosis) => {
  const regex = /\\(.?)\\*/;
  const match = diagnosis.match(regex);

  if (match && match[1]) {
    const condition = match[1];
    const parts = diagnosis.split(regex);
    return (
      <>
        {parts[0]}
        <strong>{condition}</strong>
        {parts[2]}
      </>
    );
  }
  return diagnosis;
};

export default function DiagnosisPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const savedDiagnosis = JSON.parse(localStorage.getItem('diagnosis')) || {};
  const { diagnosis, recommendations } =
    location.state || savedDiagnosis || {
      diagnosis: 'No diagnosis available',
      recommendations: 'No recommendations available',
    };

  const [lang, setLang] = useState('en-US');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [availableVoices, setAvailableVoices] = useState([]);
  const [translated, setTranslated] = useState({ diagnosis: '', recommendations: '' });
  const [isTranslating, setIsTranslating] = useState(false);
  const utteranceRef = useRef(null);

  useEffect(() => {
    const loadVoices = () => {
      const voices = window.speechSynthesis.getVoices();
      setAvailableVoices(voices);
    };
    loadVoices();
    window.speechSynthesis.onvoiceschanged = loadVoices;
  }, []);

  const getVoiceByLang = (langCode) => {
    return availableVoices.find((voice) => voice.lang === langCode) || null;
  };

  const speakText = () => {
    window.speechSynthesis.cancel();

    let textToSpeak;
    if (lang === 'hi-IN') {
      textToSpeak =
        translated.diagnosis && translated.recommendations
          ? `${translated.diagnosis}. ${translated.recommendations}`
          : `${diagnosis}. ${recommendations}`;
    } else {
      textToSpeak = `${diagnosis}. ${recommendations}`;
    }

    const utterance = new SpeechSynthesisUtterance(textToSpeak);
    utterance.lang = lang;
    const selectedVoice = getVoiceByLang(lang);
    if (selectedVoice) utterance.voice = selectedVoice;

    utterance.rate = 0.9;
    utterance.pitch = 1;
    utteranceRef.current = utterance;

    window.speechSynthesis.speak(utterance);
    setIsSpeaking(true);
    setIsPaused(false);

    utterance.onend = () => {
      setIsSpeaking(false);
      setIsPaused(false);
    };
  };

  const pauseOrResume = () => {
    if (window.speechSynthesis.speaking) {
      if (!isPaused) {
        window.speechSynthesis.pause();
        setIsPaused(true);
      } else {
        window.speechSynthesis.resume();
        setIsPaused(false);
      }
    }
  };

  const stopSpeaking = () => {
    window.speechSynthesis.cancel();
    setIsSpeaking(false);
    setIsPaused(false);
  };

  const toggleLanguage = () => {
    stopSpeaking();
    setLang((prev) => (prev === 'en-US' ? 'hi-IN' : 'en-US'));
  };

  const handleBack = () => {
    stopSpeaking();
    navigate('/');
  };

  const handleFeedback = () => {
    stopSpeaking();
    navigate('/feedback', { state: { diagnosis, recommendations } });
  };

  const translateToHindi = async () => {
    if (isTranslating) return;

    setIsTranslating(true);
    try {
      const diagnosisRes = await fetch(
        `https://translate.googleapis.com/translate_a/single?client=gtx&sl=en&tl=hi&dt=t&q=${encodeURIComponent(
          diagnosis
        )}`
      );
      const diagnosisData = await diagnosisRes.json();
      const translatedDiagnosis = diagnosisData[0].map((item) => item[0]).join(' ');

      const recommendationsRes = await fetch(
        `https://translate.googleapis.com/translate_a/single?client=gtx&sl=en&tl=hi&dt=t&q=${encodeURIComponent(
          recommendations
        )}`
      );
      const recommendationsData = await recommendationsRes.json();
      const translatedRecommendations = recommendationsData[0].map((item) => item[0]).join(' ');

      setTranslated({
        diagnosis: translatedDiagnosis,
        recommendations: translatedRecommendations,
      });
    } catch (err) {
      console.error('Translation failed:', err);
      setTranslated({
        diagnosis,
        recommendations,
      });
    } finally {
      setIsTranslating(false);
    }
  };

  return (
    <div className="flex flex-row w-screen h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <div className="w-1/2 h-full border-2 border-gray-200 rounded-lg m-2 shadow-sm">
        <div className="text-center text-gray-700 font-semibold text-lg mt-1">BODY (readonly)</div>
        <Canvas
          camera={{ position: [0, 0, 10], fov: 50, near: 0.1, far: 100 }}
          gl={{ antialias: true }}
          shadows
        >
          <color attach="background" args={['#808080']} />
          <ambientLight intensity={0.8} />
          <directionalLight position={[10, 10, 10]} intensity={1.5} castShadow />
          <Environment preset="studio" />
          <BodyModel onPartClick={() => {}} selectedParts={[]} />
          <OrbitControls
            enableZoom={true}
            enablePan={false}
            enableRotate={true}
            target={[0, 0, 0]}
            minDistance={2}
            maxDistance={15}
          />
        </Canvas>
      </div>

      <div className="w-1/2 p-4 m-2 border-2 border-gray-200 rounded-lg flex flex-col space-y-3 shadow-sm overflow-y-auto">
        <div className="flex flex-col space-y-3 flex-1">
          <div className="flex flex-row space-x-3">
            <div className="flex-1">
              <h2 className="text-gray-700 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">Diagnosis</h2>
              <div className="text-gray-600 text-sm">{renderDiagnosis(diagnosis)}</div>
              {translated.diagnosis && (
                <div className="text-green-700 text-sm mt-2 italic">
                  <span className="font-bold">हिंदी:</span> {translated.diagnosis}
                </div>
              )}
            </div>
            <div className="flex-1 border-l-2 border-gray-200 pl-3">
              <h2 className="text-gray-700 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">How to Fix It</h2>
              <div className="text-gray-600 text-sm">{recommendations}</div>
              {translated.recommendations && (
                <div className="text-green-700 text-sm mt-2 italic">
                  <span className="font-bold">हिंदी:</span> {translated.recommendations}
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="flex flex-row space-x-3">
          <button
            onClick={translateToHindi}
            disabled={isTranslating}
            className={`p-2 rounded-md transition w-full ${
              isTranslating
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-orange-500 hover:bg-orange-600 text-white'
            }`}
          >
            {isTranslating ? 'Translating...' : '🌍 Translate to Hindi'}
          </button>
        </div>

        <div className="flex flex-row space-x-3 mt-2">
          <button
            onClick={speakText}
            className="p-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition w-1/3"
          >
            🔊 {lang === 'en-US' ? 'Speak (English)' : 'Speak (Hindi)'}
          </button>
          <button
            onClick={pauseOrResume}
            disabled={!isSpeaking}
            className={`p-2 rounded-md transition w-1/3 ${
              !isSpeaking
                ? 'bg-gray-300 cursor-not-allowed'
                : 'bg-yellow-500 hover:bg-yellow-600 text-white'
            }`}
          >
            {isPaused ? '▶ Resume' : '⏸ Pause'}
          </button>
          <button
            onClick={toggleLanguage}
            className="p-2 bg-purple-500 text-white rounded-md hover:bg-purple-600 transition w-1/3"
          >
            🌐 {lang === 'en-US' ? 'Switch to Hindi' : 'Switch to English'}
          </button>
        </div>

        <p className="text-gray-500 text-xs italic mt-2">
          We are not medical professionals. Consult a doctor. Use at your own risk.
        </p>

        <div className="flex flex-row space-x-3 mt-3">
          <button
            onClick={handleBack}
            className="w-1/2 p-2 bg-gray-200 text-gray-700 rounded-md font-medium hover:bg-gray-300 transition shadow-sm"
          >
            Back
          </button>
          <button
            onClick={handleFeedback}
            className="w-1/2 p-2 bg-blue-500 text-white rounded-md font-medium hover:bg-blue-600 transition shadow-sm"
          >
            Feedback
          </button>
        </div>
      </div>
    </div>
  );
}
