import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Canvas } from '@react-three/fiber';
import { Environment, OrbitControls } from '@react-three/drei';
import BodyModel from './BodyModel';
import axios from 'axios';

const renderDiagnosis = (text) => {
  const parts = text.split(/\*\*(.*?)\*\*/);
  return parts.map((part, i) => 
    i % 2 === 1 ? (
      <strong key={i} className="text-purple-300">{part}</strong>
    ) : (
      <span key={i}>{part}</span>
    )
  );
};

const speakText = (text, langCode, setSpeechUtterance, setIsSpeaking) => {
  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = langCode;
  setSpeechUtterance(utterance);
  speechSynthesis.speak(utterance);
  setIsSpeaking(true);
};

const translateText = async (text, targetLang) => {
  try {
    const response = await axios.post(
      'https://libretranslate.com/translate',
      {
        q: text,
        source: 'en',
        target: targetLang,
        format: 'text',
      },
      {
        headers: { 
          'accept': 'application/json', 
          'Content-Type': 'application/json' 
        },
      }
    );
    return response.data.translatedText;
  } catch (error) {
    console.error('Translation error:', error);
    return text; // Return original text if translation fails
  }
};

export default function DiagnosisPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const savedDiagnosis = JSON.parse(localStorage.getItem('diagnosis')) || {};
  const { diagnosis, recommendations } = location.state || savedDiagnosis || {
    diagnosis: 'No diagnosis available',
    recommendations: 'No recommendations available',
  };

  const [speechUtterance, setSpeechUtterance] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [language, setLanguage] = useState('en');
  const [translatedDiagnosis, setTranslatedDiagnosis] = useState('');
  const [translatedRecommendations, setTranslatedRecommendations] = useState('');
  const [isTranslating, setIsTranslating] = useState(false);

  const handleBack = () => navigate('/');
  const handleFeedback = () => navigate('/feedback', { state: { diagnosis, recommendations } });

  const handleSpeak = () => {
    const textToSpeak = language === 'hi'
      ? `‡§®‡§ø‡§¶‡§æ‡§®: ${showDiagnosis.replace(/\*\*/g, '')}. ‡§∏‡§ø‡§´‡§æ‡§∞‡§ø‡§∂‡•á‡§Ç: ${showRecommendations}`
      : `Diagnosis: ${showDiagnosis.replace(/\*\*/g, '')}. Recommendations: ${showRecommendations}`;
    const langCode = language === 'hi' ? 'hi-IN' : 'en-US';
    speakText(textToSpeak, langCode, setSpeechUtterance, setIsSpeaking);
  };

  const handlePause = () => {
    speechSynthesis.pause();
    setIsPaused(true);
  };

  const handleResume = () => {
    speechSynthesis.resume();
    setIsPaused(false);
  };

  const handleStop = () => {
    speechSynthesis.cancel();
    setIsSpeaking(false);
    setIsPaused(false);
  };

  const toggleLanguage = async () => {
    const newLang = language === 'en' ? 'hi' : 'en';
    setLanguage(newLang);

    // Force content translation when switching to Hindi
    if (newLang === 'hi') {
      setIsTranslating(true);
      try {
        const [diag, reco] = await Promise.all([
          translateText(diagnosis, 'hi'),
          translateText(recommendations, 'hi')
        ]);
        setTranslatedDiagnosis(diag);
        setTranslatedRecommendations(reco);
      } catch (error) {
        console.error('Translation failed:', error);
        // Fallback to English if translation fails
        setTranslatedDiagnosis(diagnosis);
        setTranslatedRecommendations(recommendations);
      } finally {
        setIsTranslating(false);
      }
    }
  };

  const translateContent = async () => {
    if (language === 'hi' && (!translatedDiagnosis || !translatedRecommendations)) {
      setIsTranslating(true);
      try {
        const [diag, reco] = await Promise.all([
          translateText(diagnosis, 'hi'),
          translateText(recommendations, 'hi')
        ]);
        setTranslatedDiagnosis(diag);
        setTranslatedRecommendations(reco);
      } catch (error) {
        console.error('Translation failed:', error);
        setTranslatedDiagnosis(diagnosis);
        setTranslatedRecommendations(recommendations);
      } finally {
        setIsTranslating(false);
      }
    }
  };

  useEffect(() => {
    if (language === 'hi' && !translatedDiagnosis) {
      toggleLanguage();
    }
  }, []);

  const showDiagnosis = language === 'hi' ? translatedDiagnosis : diagnosis;
  const showRecommendations = language === 'hi' ? translatedRecommendations : recommendations;

  return (
    <div className="dark-theme flex flex-row w-screen h-screen bg-gray-900">
      {/* Left Panel - 3D Body */}
      <div className="w-1/2 h-full border-2 border-gray-700 rounded-lg m-2">
        <div className="text-center text-white font-semibold text-lg mt-1">
          {language === 'hi' ? '‡§∂‡§∞‡•Ä‡§∞' : '3D Body Model'}
        </div>
        <Canvas
          camera={{ position: [0, 0, 10], fov: 50, near: 0.1, far: 100 }}
          gl={{ antialias: true }}
          shadows
        >
          <color attach="background" args={['#1a1a1a']} />
          <ambientLight intensity={0.8} />
          <directionalLight position={[10, 10, 10]} intensity={1.5} castShadow />
          <Environment preset="studio" />
          <BodyModel onPartClick={() => {}} selectedParts={[]} />
          <OrbitControls enableZoom enableRotate target={[0, 0, 0]} minDistance={2} maxDistance={15} />
        </Canvas>
      </div>

      {/* Right Panel - Diagnosis Content */}
      <div className="w-1/2 p-4 m-2 border-2 border-gray-700 rounded-lg flex flex-col space-y-4 bg-gray-800">
        {isTranslating && (
          <div className="text-center py-2 text-yellow-400">
            {language === 'hi' ? '‡§Ö‡§®‡•Å‡§µ‡§æ‡§¶ ‡§π‡•ã ‡§∞‡§π‡§æ ‡§π‡•à...' : 'Translating...'}
          </div>
        )}

        {/* Diagnosis Section */}
        <div className="space-y-4">
          <h2 className="text-white font-semibold text-xl border-b border-gray-600 pb-2">
            {language === 'hi' ? '‡§®‡§ø‡§¶‡§æ‡§®' : 'Diagnosis'}
          </h2>
          <div className="text-gray-200 text-base leading-relaxed">
            {renderDiagnosis(showDiagnosis)}
          </div>
        </div>

        {/* Treatment Section */}
        <div className="space-y-4">
          <h2 className="text-white font-semibold text-xl border-b border-gray-600 pb-2">
            {language === 'hi' ? '‡§â‡§™‡§ö‡§æ‡§∞' : 'How to Fix It'}
          </h2>
          <div className="text-gray-200 text-base leading-relaxed">
            {showRecommendations}
          </div>
        </div>

        {/* Footer */}
        <p className="text-gray-400 text-xs italic mt-auto pt-4 border-t border-gray-700">
          {language === 'hi'
            ? '‡§Ø‡§π ‡§µ‡•á‡§¨ ‡§ê‡§™ ‡§µ‡§ø‡§ï‡§æ‡§∏ ‡§ö‡§∞‡§£ ‡§Æ‡•á‡§Ç ‡§π‡•à (Zodrick John & Wood Jones ‡§¶‡•ç‡§µ‡§æ‡§∞‡§æ)'
            : 'This web app is in development (By Zodrick John & Wood Jones)'}
        </p>

        {/* Action Buttons */}
        <div className="flex space-x-3 mt-4">
          <button 
            onClick={handleBack}
            className="flex-1 py-2 bg-gray-700 text-white rounded hover:bg-gray-600 transition"
          >
            {language === 'hi' ? '‡§µ‡§æ‡§™‡§∏' : 'Back'}
          </button>

          <button 
            onClick={handleFeedback}
            className="flex-1 py-2 bg-blue-600 text-white rounded hover:bg-blue-500 transition"
          >
            {language === 'hi' ? '‡§™‡•ç‡§∞‡§§‡§ø‡§ï‡•ç‡§∞‡§ø‡§Ø‡§æ' : 'Feedback'}
          </button>

          {isSpeaking ? (
            <button 
              onClick={handleStop}
              className="flex-1 py-2 bg-red-600 text-white rounded hover:bg-red-500 transition"
            >
              üõë {language === 'hi' ? '‡§∞‡•ã‡§ï‡•á‡§Ç' : 'Stop'}
            </button>
          ) : (
            <button 
              onClick={handleSpeak}
              className="flex-1 py-2 bg-green-600 text-white rounded hover:bg-green-500 transition"
            >
              üîä {language === 'hi' ? '‡§¨‡•ã‡§≤‡•á‡§Ç' : 'Speak'}
            </button>
          )}
        </div>

        {/* Language Toggle */}
        <div className="mt-3 text-center">
          <button
            onClick={toggleLanguage}
            className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-500 transition shadow-sm"
            disabled={isTranslating}
          >
            {language === 'hi' ? 'üá∫üá∏ Switch to English' : 'üáÆüá≥ ‡§π‡§ø‡§Ç‡§¶‡•Ä ‡§Æ‡•á‡§Ç ‡§¶‡•á‡§ñ‡•á‡§Ç'}
          </button>
        </div>
      </div>
    </div>
  );
}
