import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Canvas } from '@react-three/fiber';
import { Environment, OrbitControls } from '@react-three/drei';
import BodyModel from './BodyModel';

const renderDiagnosis = (diagnosis) => {
  const regex = /\*\*(.*?)\*\*/;
  const match = diagnosis.match(regex);
  if (match && match[1]) {
    const condition = match[1];
    const parts = diagnosis.split(regex);
    return (
      <>
        {parts[0]}
        <strong>{condition}</strong>
        {parts[2]}
      </>
    );
  }
  return diagnosis;
};

const speakText = (text, lang, setSpeechUtterance, setIsSpeaking) => {
  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = lang;
  setSpeechUtterance(utterance);
  speechSynthesis.speak(utterance);
  setIsSpeaking(true);
};

// Basic mock translation for demo
const translateToHindi = (text) => {
  return text
    .replace('Diagnosis', '‡§®‡§ø‡§¶‡§æ‡§®')
    .replace('Recommendations', '‡§∏‡§ø‡§´‡§æ‡§∞‡§ø‡§∂‡•á‡§Ç')
    .replace('No diagnosis available', '‡§ï‡•ã‡§à ‡§®‡§ø‡§¶‡§æ‡§® ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§®‡§π‡•Ä‡§Ç ‡§π‡•à')
    .replace('No recommendations available', '‡§ï‡•ã‡§à ‡§∏‡§ø‡§´‡§æ‡§∞‡§ø‡§∂‡•á‡§Ç ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§®‡§π‡•Ä‡§Ç ‡§π‡•à‡§Ç');
};

export default function DiagnosisPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const savedDiagnosis = JSON.parse(localStorage.getItem('diagnosis')) || {};
  const { diagnosis, recommendations } = location.state || savedDiagnosis || {
    diagnosis: 'No diagnosis available',
    recommendations: 'No recommendations available',
  };

  const [speechUtterance, setSpeechUtterance] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [language, setLanguage] = useState('en'); // 'en' or 'hi'

  const handleBack = () => navigate('/');
  const handleFeedback = () => navigate('/feedback', { state: { diagnosis, recommendations } });

  const handleSpeak = () => {
    const baseDiagnosis = diagnosis.replace(/\*\*/g, '');
    const textToSpeak =
      language === 'hi'
        ? `‡§®‡§ø‡§¶‡§æ‡§®: ${translateToHindi(baseDiagnosis)}. ‡§∏‡§ø‡§´‡§æ‡§∞‡§ø‡§∂‡•á‡§Ç: ${translateToHindi(recommendations)}`
        : `Diagnosis: ${baseDiagnosis}. Recommendations: ${recommendations}`;
    const langCode = language === 'hi' ? 'hi-IN' : 'en-US';
    speakText(textToSpeak, langCode, setSpeechUtterance, setIsSpeaking);
  };

  const handlePause = () => {
    if (speechUtterance) {
      speechSynthesis.pause();
      setIsPaused(true);
    }
  };

  const handleResume = () => {
    if (speechUtterance) {
      speechSynthesis.resume();
      setIsPaused(false);
    }
  };

  const handleStop = () => {
    if (speechUtterance) {
      speechSynthesis.cancel();
      setIsSpeaking(false);
      setIsPaused(false);
    }
  };

  const toggleLanguage = () => {
    setLanguage((prev) => (prev === 'en' ? 'hi' : 'en'));
  };

  const renderText = (text) => (language === 'hi' ? translateToHindi(text) : text);

  return (
    <div className="flex flex-row w-screen h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <div className="w-1/2 h-full border-2 border-gray-200 rounded-lg m-2 shadow-sm">
        <div className="text-center text-gray-700 font-semibold text-lg mt-1">BODY (readonly)</div>
        <Canvas
          camera={{ position: [0, 0, 10], fov: 50, near: 0.1, far: 100 }}
          gl={{ antialias: true }}
          shadows
        >
          <color attach="background" args={['#808080']} />
          <ambientLight intensity={0.8} />
          <directionalLight position={[10, 10, 10]} intensity={1.5} castShadow />
          <Environment preset="studio" />
          <BodyModel onPartClick={() => {}} selectedParts={[]} />
          <OrbitControls
            enableZoom={true}
            enablePan={false}
            enableRotate={true}
            target={[0, 0, 0]}
            minDistance={2}
            maxDistance={15}
          />
        </Canvas>
      </div>
      <div className="w-1/2 p-4 m-2 border-2 border-gray-200 rounded-lg flex flex-col space-y-3 shadow-sm">
        <div className="flex flex-col space-y-3 flex-1">
          <div className="flex flex-row justify-between">
            <h2 className="text-gray-700 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">
              {language === 'hi' ? '‡§®‡§ø‡§¶‡§æ‡§®' : 'Diagnosis'}
            </h2>
            <button
              onClick={toggleLanguage}
              className="px-3 py-1 bg-purple-500 text-white rounded-md hover:bg-purple-600 transition text-sm"
            >
              {language === 'hi' ? 'Switch to English' : 'Switch to Hindi'}
            </button>
          </div>
          <div className="text-gray-600 text-sm">{renderDiagnosis(renderText(diagnosis))}</div>
          <h2 className="text-gray-700 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">
            {language === 'hi' ? '‡§á‡§∏‡•á ‡§ï‡•à‡§∏‡•á ‡§†‡•Ä‡§ï ‡§ï‡§∞‡•á‡§Ç' : 'How to Fix It'}
          </h2>
          <div className="text-gray-600 text-sm">{renderText(recommendations)}</div>
        </div>
        <p className="text-gray-500 text-xs italic mt-2">
          {language === 'hi'
            ? '‡§π‡§Æ ‡§ö‡§ø‡§ï‡§ø‡§§‡•ç‡§∏‡§æ ‡§™‡•á‡§∂‡•á‡§µ‡§∞ ‡§®‡§π‡•Ä‡§Ç ‡§π‡•à‡§Ç‡•§ ‡§ï‡•É‡§™‡§Ø‡§æ ‡§°‡•â‡§ï‡•ç‡§ü‡§∞ ‡§∏‡•á ‡§™‡§∞‡§æ‡§Æ‡§∞‡•ç‡§∂ ‡§≤‡•á‡§Ç‡•§ ‡§Ö‡§™‡§®‡•á ‡§ú‡•ã‡§ñ‡§ø‡§Æ ‡§™‡§∞ ‡§â‡§™‡§Ø‡•ã‡§ó ‡§ï‡§∞‡•á‡§Ç‡•§'
            : 'We are not medical professionals. Consult a doctor. Use at your own risk.'}
        </p>
        <div className="flex flex-row space-x-3 mt-3">
          <button
            onClick={handleBack}
            className="w-1/3 p-2 bg-gray-200 text-gray-700 rounded-md font-medium hover:bg-gray-300 transition shadow-sm"
          >
            {language === 'hi' ? '‡§µ‡§æ‡§™‡§∏' : 'Back'}
          </button>
          <button
            onClick={handleFeedback}
            className="w-1/3 p-2 bg-blue-500 text-white rounded-md font-medium hover:bg-blue-600 transition shadow-sm"
          >
            {language === 'hi' ? '‡§™‡•ç‡§∞‡§§‡§ø‡§ï‡•ç‡§∞‡§ø‡§Ø‡§æ' : 'Feedback'}
          </button>
          {isSpeaking ? (
            <button
              onClick={handleStop}
              className="w-1/3 p-2 bg-red-500 text-white rounded-md font-medium hover:bg-red-600 transition shadow-sm"
            >
              üõë {language === 'hi' ? '‡§∞‡•ã‡§ï‡•á‡§Ç' : 'Stop'}
            </button>
          ) : (
            <button
              onClick={handleSpeak}
              className="w-1/3 p-2 bg-green-500 text-white rounded-md font-medium hover:bg-green-600 transition shadow-sm"
            >
              üîä {language === 'hi' ? '‡§¨‡•ã‡§≤‡•á‡§Ç' : 'Speak'}
            </button>
          )}
          {isSpeaking && !isPaused ? (
            <button
              onClick={handlePause}
              className="w-1/3 p-2 bg-yellow-500 text-white rounded-md font-medium hover:bg-yellow-600 transition shadow-sm"
            >
              ‚è∏Ô∏è {language === 'hi' ? '‡§µ‡§ø‡§∞‡§æ‡§Æ' : 'Pause'}
            </button>
          ) : (
            isPaused && (
              <button
                onClick={handleResume}
                className="w-1/3 p-2 bg-orange-500 text-white rounded-md font-medium hover:bg-orange-600 transition shadow-sm"
              >
                ‚ñ∂Ô∏è {language === 'hi' ? '‡§ú‡§æ‡§∞‡•Ä ‡§∞‡§ñ‡•á‡§Ç' : 'Resume'}
              </button>
            )
          )}
        </div>
      </div>
    </div>
  );
}
