import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Canvas } from '@react-three/fiber';
import { Environment, OrbitControls } from '@react-three/drei';
import BodyModel from './BodyModel';
import axios from 'axios';

// Bold the condition name in diagnosis
const renderDiagnosis = (diagnosis) => {
  const regex = /\*\*(.*?)\*\*/;
  const match = diagnosis.match(regex);
  if (match && match[1]) {
    const condition = match[1];
    const parts = diagnosis.split(regex);
    return (
      <>
        {parts[0]}
        <strong className="highlighted-condition">{condition}</strong>
        {parts[2]}
      </>
    );
  }
  return diagnosis;
};

// üîä Speak text in the correct language
const speakText = (text, langCode, setSpeechUtterance, setIsSpeaking) => {
  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = langCode;
  setSpeechUtterance(utterance);
  speechSynthesis.speak(utterance);
  setIsSpeaking(true);
};

// üåê Translate text using LibreTranslate API
const translateText = async (text, targetLang) => {
  try {
    const response = await axios.post(
      'https://libretranslate.com/translate',
      {
        q: text,
        source: 'en',
        target: targetLang,
        format: 'text',
      },
      {
        headers: { 'accept': 'application/json', 'Content-Type': 'application/json' },
      }
    );
    return response.data.translatedText;
  } catch (error) {
    console.error('Translation error:', error);
    return text;
  }
};

export default function DiagnosisPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const savedDiagnosis = JSON.parse(localStorage.getItem('diagnosis')) || {};
  const { diagnosis, recommendations } = location.state || savedDiagnosis || {
    diagnosis: 'No diagnosis available',
    recommendations: 'No recommendations available',
  };

  const [speechUtterance, setSpeechUtterance] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [language, setLanguage] = useState('en');
  const [translatedDiagnosis, setTranslatedDiagnosis] = useState('');
  const [translatedRecommendations, setTranslatedRecommendations] = useState('');

  const handleBack = () => navigate('/');
  const handleFeedback = () => navigate('/feedback', { state: { diagnosis, recommendations } });

  // üó£Ô∏è Speak according to selected language
  const handleSpeak = () => {
    const textToSpeak =
      language === 'hi'
        ? `‡§®‡§ø‡§¶‡§æ‡§®: ${translatedDiagnosis.replace(/\*\*/g, '')}. ‡§∏‡§ø‡§´‡§æ‡§∞‡§ø‡§∂‡•á‡§Ç: ${translatedRecommendations}`
        : `Diagnosis: ${diagnosis.replace(/\*\*/g, '')}. Recommendations: ${recommendations}`;
    const langCode = language === 'hi' ? 'hi-IN' : 'en-US';
    speakText(textToSpeak, langCode, setSpeechUtterance, setIsSpeaking);
  };

  const handlePause = () => {
    speechSynthesis.pause();
    setIsPaused(true);
  };

  const handleResume = () => {
    speechSynthesis.resume();
    setIsPaused(false);
  };

  const handleStop = () => {
    speechSynthesis.cancel();
    setIsSpeaking(false);
    setIsPaused(false);
  };

  const toggleLanguage = () => {
    setLanguage((prev) => (prev === 'en' ? 'hi' : 'en'));
  };

  // Translate when language changes to Hindi
  useEffect(() => {
    const fetchTranslation = async () => {
      if (language === 'hi') {
        const translatedDiag = await translateText(diagnosis, 'hi');
        const translatedReco = await translateText(recommendations, 'hi');
        setTranslatedDiagnosis(translatedDiag);
        setTranslatedRecommendations(translatedReco);
      }
    };
    fetchTranslation();
  }, [language]);

  const showDiagnosis = language === 'hi' ? translatedDiagnosis : diagnosis;
  const showRecommendations = language === 'hi' ? translatedRecommendations : recommendations;

  return (
    <div className="dark-theme flex flex-row w-screen h-screen bg-gray-900">
      {/* Left Panel - 3D Body */}
      <div className="w-1/2 h-full border-2 border-gray-700 rounded-lg m-2">
        <div className="text-center text-white font-semibold text-lg mt-1">
          {language === 'hi' ? '‡§∂‡§∞‡•Ä‡§∞' : 'BODY'}
        </div>
        <Canvas>
          <color attach="background" args={['#1a1a1a']} />
          {/* ... other canvas elements ... */}
        </Canvas>
      </div>

      {/* Right Panel - Diagnosis Content */}
      <div className="w-1/2 p-4 m-2 border-2 border-gray-700 rounded-lg flex flex-col space-y-4 bg-gray-800">
        {/* Diagnosis Section */}
        <div className="space-y-4">
          <h2 className="text-white font-semibold text-xl border-b border-gray-600 pb-2">
            {language === 'hi' ? '‡§®‡§ø‡§¶‡§æ‡§®' : 'Diagnosis'}
          </h2>
          <div className="text-gray-200 text-base leading-relaxed">
            {renderDiagnosis(showDiagnosis)}
          </div>
        </div>

        {/* Treatment Section */}
        <div className="space-y-4">
          <h2 className="text-white font-semibold text-xl border-b border-gray-600 pb-2">
            {language === 'hi' ? '‡§â‡§™‡§ö‡§æ‡§∞' : 'How to Fix It'}
          </h2>
          <div className="text-gray-200 text-base leading-relaxed">
            {showRecommendations}
          </div>
        </div>

        {/* Footer */}
        <p className="text-gray-400 text-xs italic mt-auto pt-4 border-t border-gray-700">
          {language === 'hi' 
            ? '‡§Ø‡§π ‡§µ‡•á‡§¨ ‡§ê‡§™ ‡§µ‡§ø‡§ï‡§æ‡§∏ ‡§ö‡§∞‡§£ ‡§Æ‡•á‡§Ç ‡§π‡•à (Zodrick John & Wood Jones ‡§¶‡•ç‡§µ‡§æ‡§∞‡§æ)'
            : 'This web app is in development phase (By Zodrick John & Wood Jones)'}
        </p>

        {/* Action Buttons */}
        <div className="flex space-x-3 mt-4">
          <button className="flex-1 py-2 bg-gray-700 text-white rounded hover:bg-gray-600 transition">
            {language === 'hi' ? '‡§µ‡§æ‡§™‡§∏' : 'Back'}
          </button>
          <button className="flex-1 py-2 bg-blue-600 text-white rounded hover:bg-blue-500 transition">
            {language === 'hi' ? '‡§™‡•ç‡§∞‡§§‡§ø‡§ï‡•ç‡§∞‡§ø‡§Ø‡§æ' : 'Feedback'}
          </button>
          <button className="flex-1 py-2 bg-green-600 text-white rounded hover:bg-green-500 transition">
            {language === 'hi' ? '‡§¨‡•ã‡§≤‡•á‡§Ç' : 'Speak'}
          </button>
        </div>
      </div>
    </div>
  );
}
