import React, { useState, useEffect, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Canvas } from '@react-three/fiber';
import { Environment, OrbitControls } from '@react-three/drei';
import BodyModel from './BodyModel';

const renderDiagnosis = (diagnosis) => {
  const regex = /\\(.*?)\\*/;
  const match = diagnosis.match(regex);
  if (match && match[1]) {
    const condition = match[1];
    const parts = diagnosis.split(regex);
    return (
      <>
        {parts[0]}
        <strong>{condition}</strong>
        {parts[2]}
      </>
    );
  }
  return diagnosis;
};

export default function DiagnosisPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const savedDiagnosis = JSON.parse(localStorage.getItem('diagnosis')) || {};
  const { diagnosis, recommendations } = location.state || savedDiagnosis || {
    diagnosis: 'No diagnosis available',
    recommendations: 'No recommendations available',
  };

  const [lang, setLang] = useState('en-US');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [availableVoices, setAvailableVoices] = useState([]);
  const [translated, setTranslated] = useState({ diagnosis: '', recommendations: '' });
  const [isTranslating, setIsTranslating] = useState(false);
  const utteranceRef = useRef(null);

  useEffect(() => {
    const loadVoices = () => {
      const voices = window.speechSynthesis.getVoices();
      setAvailableVoices(voices);
    };
    loadVoices();
    window.speechSynthesis.onvoiceschanged = loadVoices;
  }, []);

  useEffect(() => {
    if (lang === 'hi-IN') {
      translateToHindi();
    }
  }, [lang]);

  const getVoiceByLang = (langCode) => {
    return availableVoices.find((voice) => voice.lang === langCode) || null;
  };

  const speakText = () => {
    window.speechSynthesis.cancel();

    let textToSpeak;
    if (lang === 'hi-IN') {
      textToSpeak = translated.diagnosis && translated.recommendations
        ? `${translated.diagnosis}. ${translated.recommendations}`
        : `${diagnosis}. ${recommendations}`;
    } else {
      textToSpeak = `${diagnosis}. ${recommendations}`;
    }

    const utterance = new SpeechSynthesisUtterance(textToSpeak);
    utterance.lang = lang;
    const selectedVoice = getVoiceByLang(lang);
    if (selectedVoice) utterance.voice = selectedVoice;

    utterance.rate = 0.9;
    utterance.pitch = 1;
    utteranceRef.current = utterance;

    window.speechSynthesis.speak(utterance);
    setIsSpeaking(true);
    setIsPaused(false);

    utterance.onend = () => {
      setIsSpeaking(false);
      setIsPaused(false);
    };
  };

  const pauseOrResume = () => {
    if (window.speechSynthesis.speaking) {
      if (!isPaused) {
        window.speechSynthesis.pause();
        setIsPaused(true);
      } else {
        window.speechSynthesis.resume();
        setIsPaused(false);
      }
    }
  };

  const stopSpeaking = () => {
    window.speechSynthesis.cancel();
    setIsSpeaking(false);
    setIsPaused(false);
  };

  const toggleLanguage = () => {
    stopSpeaking();
    setLang((prev) => (prev === 'en-US' ? 'hi-IN' : 'en-US'));
  };

  const handleBack = () => {
    stopSpeaking();
    navigate('/');
  };

  const handleFeedback = () => {
    stopSpeaking();
    navigate('/feedback', { state: { diagnosis, recommendations } });
  };

  const translateToHindi = async () => {
    if (isTranslating) return;

    setIsTranslating(true);
    try {
      const diagnosisRes = await fetch(
        `https://translate.googleapis.com/translate_a/single?client=gtx&sl=en&tl=hi&dt=t&q=${encodeURIComponent(diagnosis)}`
      );
      const diagnosisData = await diagnosisRes.json();
      const translatedDiagnosis = diagnosisData[0].map(item => item[0]).join(' ');

      const recommendationsRes = await fetch(
        `https://translate.googleapis.com/translate_a/single?client=gtx&sl=en&tl=hi&dt=t&q=${encodeURIComponent(recommendations)}`
      );
      const recommendationsData = await recommendationsRes.json();
      const translatedRecommendations = recommendationsData[0].map(item => item[0]).join(' ');

      setTranslated({
        diagnosis: translatedDiagnosis,
        recommendations: translatedRecommendations
      });
    } catch (err) {
      console.error('Translation failed:', err);
      setTranslated({ diagnosis, recommendations });
    } finally {
      setIsTranslating(false);
    }
  };

  const showDiagnosis = lang === 'hi-IN' ? translated.diagnosis : diagnosis;
  const showRecommendations = lang === 'hi-IN' ? translated.recommendations : recommendations;

  return (
    <div className="flex flex-row w-screen h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <div className="w-1/2 h-full border-2 border-gray-200 rounded-lg m-2 shadow-sm">
        <div className="text-center text-white-800 font-semibold text-lg mt-1">
          {lang === 'hi-IN' ? 'शरीर (केवल पढ़ने के लिए)' : '3D Human Body Model'}
        </div>
        <Canvas
          camera={{ position: [0, 0, 10], fov: 50, near: 0.1, far: 100 }}
          gl={{ antialias: true }}
          shadows
        >
          <color attach="background" args={['#000000']} />
          <ambientLight intensity={0.8} />
          <directionalLight position={[10, 10, 10]} intensity={0} castShadow />
          <Environment preset="studio" />
          <BodyModel onPartClick={() => {}} selectedParts={[]} />
          <OrbitControls enableZoom enableRotate target={[0, 0, 0]} minDistance={2} maxDistance={15} />
        </Canvas>
      </div>

      <div className="w-1/2 p-4 m-2 border-2 border-gray-200 rounded-lg flex flex-col space-y-3 shadow-sm">
        <div className="flex flex-col space-y-3 flex-1">
          <div className="flex flex-row space-x-3">
            <div className="flex-1">
              <h2 className="text-white-800 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">
                {lang === 'hi-IN' ? 'निदान' : 'Diagnosis'}
              </h2>
              <div className="text-gray-600 text-sm">{renderDiagnosis(showDiagnosis)}</div>
            </div>
            <div className="flex-1 border-l-2 border-gray-200 pl-3">
              <h2 className="text-white-800 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">
                {lang === 'hi-IN' ? 'इसे कैसे ठीक करें' : 'How to Fix It'}
              </h2>
              <div className="text-gray-600 text-sm">{showRecommendations}</div>
            </div>
          </div>
        </div>

        <p className="text-gray-500 text-xs italic mt-2">
          {lang === 'hi-IN'
            ? 'यह वेब ऐप विकास चरण में है [बनाया गया Zodrick John और Mohd Aanas द्वारा]'
            : 'This web app is in the developing phase [Made by Zodrick John & Mohd Aanas]'}
        </p>

        <div className="flex flex-row space-x-3 mt-3">
          <button onClick={handleBack} className="w-1/4 p-2 bg-gray-200 text-gray-700 rounded-md font-medium hover:bg-gray-300 transition shadow-sm">
            {lang === 'hi-IN' ? 'वापस' : 'Back'}
          </button>

          <button onClick={handleFeedback} className="w-1/4 p-2 bg-blue-500 text-white rounded-md font-medium hover:bg-blue-600 transition shadow-sm">
            {lang === 'hi-IN' ? 'प्रतिक्रिया' : 'Feedback'}
          </button>

          {isSpeaking ? (
            <button onClick={stopSpeaking} className="w-1/4 p-2 bg-red-500 text-white rounded-md font-medium hover:bg-red-600 transition shadow-sm">
              🛑 {lang === 'hi-IN' ? 'रुकें' : 'Stop'}
            </button>
          ) : (
            <button onClick={speakText} className="w-1/4 p-2 bg-green-500 text-white rounded-md font-medium hover:bg-green-600 transition shadow-sm">
              🔊 {lang === 'hi-IN' ? 'बोलें' : 'Speak'}
            </button>
          )}

          {isSpeaking && (
            <button onClick={pauseOrResume} className="w-1/4 p-2 bg-yellow-500 text-white rounded-md font-medium hover:bg-yellow-600 transition shadow-sm">
              {isPaused ? '▶️ ' + (lang === 'hi-IN' ? 'फिर से शुरू करें' : 'Resume') : '⏸️ ' + (lang === 'hi-IN' ? 'रोकें' : 'Pause')}
            </button>
          )}
        </div>

        <div className="mt-3 text-center">
          <button
            onClick={toggleLanguage}
            className="px-4 py-1 bg-purple-500 text-white rounded-md hover:bg-purple-600 transition shadow-sm"
          >
            {lang === 'hi-IN' ? '🇺🇸 English में देखें' : '🇮🇳 हिंदी में देखें'}
          </button>
        </div>
      </div>
    </div>
  );
}
