import React, { useEffect, useState } from 'react';
import { speakText, pauseSpeech, resumeSpeech, stopSpeech } from '@/lib/voice';
import { translateText } from '@/lib/translate';
import { Button } from '@/components/ui/button';

const DiagnosisPage = ({ diagnosis, recommendations }) => {
  const [translatedDiagnosis, setTranslatedDiagnosis] = useState('');
  const [translatedRecommendations, setTranslatedRecommendations] = useState('');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [language, setLanguage] = useState('en'); // Only for speech control
  const [speechUtterance, setSpeechUtterance] = useState(null);

  useEffect(() => {
    const fetchTranslation = async () => {
      const translatedDiag = await translateText(diagnosis, 'hi');
      const translatedReco = await translateText(recommendations, 'hi');
      setTranslatedDiagnosis(translatedDiag);
      setTranslatedRecommendations(translatedReco);
    };
    fetchTranslation();
  }, [diagnosis, recommendations]);

  const handleSpeak = () => {
    const textToSpeak =
      language === 'hi'
        ? `निदान: ${translatedDiagnosis.replace(/\*\*/g, '')}. सिफारिशें: ${translatedRecommendations}`
        : `Diagnosis: ${diagnosis.replace(/\*\*/g, '')}. Recommendations: ${recommendations}`;

    const langCode = language === 'hi' ? 'hi-IN' : 'en-US';
    speakText(textToSpeak, langCode, setSpeechUtterance, setIsSpeaking);
  };

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <div className="mb-4">
        <h1 className="text-2xl font-bold text-center text-blue-700">AI Diagnosis Summary</h1>
        <div className="mt-3 text-center">
          <label className="mr-2 font-medium text-gray-600">🔈 Speak in:</label>
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            className="p-1 border rounded-md bg-white shadow-sm"
          >
            <option value="en">English</option>
            <option value="hi">हिंदी</option>
          </select>
        </div>
        <div className="flex justify-center space-x-3 mt-4">
          <Button onClick={handleSpeak} disabled={isSpeaking}>Speak</Button>
          <Button onClick={pauseSpeech}>Pause</Button>
          <Button onClick={resumeSpeech}>Resume</Button>
          <Button onClick={stopSpeech}>Stop</Button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="flex-1">
          <h2 className="text-gray-700 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">Diagnosis / निदान</h2>
          <div className="text-gray-600 text-sm mb-2">{diagnosis}</div>
          <div className="text-gray-600 text-sm italic">{translatedDiagnosis}</div>
        </div>
        <div className="flex-1 border-l-2 border-gray-200 pl-3">
          <h2 className="text-gray-700 font-semibold text-lg mb-1 border-b-2 border-gray-200 pb-1">How to Fix It / इसे कैसे ठीक करें</h2>
          <div className="text-gray-600 text-sm mb-2">{recommendations}</div>
          <div className="text-gray-600 text-sm italic">{translatedRecommendations}</div>
        </div>
      </div>
    </div>
  );
};

export default DiagnosisPage;
